rownum<=#{pageNum} * #{amount} and TITLE like '%'||#{keyword}||'%' or CONTENT like '%'||#{keyword}||'%' or ADDRESS like '%'||#{keyword}||'%' )
where rn > (#{pageNum}-1) * #{amount}

      SELECT * FROM PARTY
      WHERE TYPE=#{type}
      
      

        
            
                AND CAT_MAIN IN 
                
                    #{category}
                
            
        

      
      
       
         
